mapper.update:

     update tb_acc 
      
            froze_amnt = froze_amnt + #{addAmnt},
            total_amnt = total_amnt + #{addAmnt},
            last_caldate = now(),
            modify_time = now(),
       
            operator = #{operator}
            
     
     where user_code = #{userCode}     
           and syscode = #{syscode}
