mapper.update:
update tb_sms
set sms_status = #{smsStatus,jdbcType=VARCHAR}
where flow_id = #{flowId,jdbcType=BIGINT}

mapper.select:
select 

,

from tb_sms
where 1=1

    and sms_status = #{pd.sms_status,jdbcType=TIMESTAMP}


    and sms_type = #{pd.sms_type,jdbcType=TIMESTAMP}


    and title like '%${pd.title}%'


    and operator_accno like '%${pd.operator_accno}%'


    and operator_name like '%${pd.operator_name}%'


    and create_time >= #{pd.startTime}


    and substring(create_time,1,10) <= #{pd.endTime}

order by create_time desc

select a.user_type,a.phone,a.real_name,a.idno,a.create_time,a.ref_phone,b.real_name ref_real_name from tb_user_detail a left join tb_user_detail b on a.ref_phone = b.phone where 1=1
and a.freeze_flag = #{pd.freeze_flag}
order by a.create_time desc
