mapper.select:
select a.*,b.real_name ref_real_name,c.total_amnt,c.avb_amnt,c.froze_amnt, (select sum(t.amnt) from tb_acc_detail t where t.sub_accno='010101' and t.user_code = a.user_code)total_reward, (select sum(t.outamnt) from tb_acc_outdetail t where t.sub_acc_no='010401' and t.user_code = a.user_code)total_out,d.`status` from tb_user_detail a LEFT JOIN tb_user_detail b on a.ref_phone = b.phone LEFT JOIN tb_acc c on a.user_code = c.user_code LEFT JOIN tb_user d ON a.user_code = d.user_code where 1=1
and a.phone like CONCAT(CONCAT('%', #{pd.phone}),'%')
and a.ref_phone like CONCAT(CONCAT('%', #{pd.ref_phone}),'%')
and d.`status` = #{pd.status}
and a.create_time >= #{pd.startTime}
and substring(a.create_time,1,10)<= #{pd.endTime}
order by a.id desc

select a.*,b.real_name ref_real_name,c.total_amnt,c.avb_amnt,c.froze_amnt,date_format(a.create_time,'%Y-%m-%d %T') create_time1, (select sum(t.amnt) from tb_acc_detail t where t.sub_accno='010101' and t.user_code = a.user_code)total_reward, (select sum(t.outamnt) from tb_acc_outdetail t where t.sub_acc_no='010401' and t.user_code = a.user_code)total_out, (case when d.`status`='1' then '有效' else '禁用' end)`status`,(case when a.buy_flag ='1' then '允许购买' else '禁止购买' end)buy_flag1,(case when a.user_type ='1' then '普通会员' else '投资机构' end)user_type1 from tb_user_detail a LEFT JOIN tb_user_detail b on a.ref_phone = b.phone LEFT JOIN tb_acc c on a.user_code = c.user_code LEFT JOIN tb_user d ON a.user_code = d.user_code where 1=1
and a.phone like CONCAT(CONCAT('%', #{phone}),'%')
and a.ref_phone like CONCAT(CONCAT('%', #{ref_phone}),'%')
and d.`status` = #{status}
and a.create_time >= #{startTime}
and substring(a.create_time,1,10)<= #{endTime}
order by a.id desc

mapper.update:

     update tb_user_detail 
      
            buy_flag = #{buy_flag},
            modify_time = now(),
       
            operator = #{operator}
            
     
     where id = #{id}

update tb_user
`status` = #{status} , modify_time = now()
where user_code = #{user_code}

update tb_user
pwdhash = #{pwdhash} , modify_time = now()
where user_code = #{user_code}
